// SPDX-License-Identifier: MIT
pragma solidity ^0.8.20;

import "@openzeppelin/contracts/access/Ownable.sol";
import "@openzeppelin/contracts/utils/Strings.sol"; // For converting uint to string for referral code

contract DShareStorage is Ownable {
    using Strings for uint256; // Use Strings library for uint256 conversions

    // --- Constants and Configuration ---
    uint256 public constant MAX_STORAGE_1GB_BYTES = 1 * 1024 * 1024 * 1024; // 1 GB
    uint256 public constant MAX_STORAGE_5GB_BYTES = 5 * 1024 * 1024 * 1024; // 5 GB
    uint256 public constant UPGRADE_TO_5GB_PRICE = 0.1 ether;
    uint256 public constant SHARE_FEE = 10000000000000; // 0.00001 ETH (10,000,000,000,000 wei)
    uint256 public constant MIN_DOWNLOAD_PRICE = 10000000000000; // 0.00001 ETH
    uint256 public constant MAX_DOWNLOAD_PRICE = 0.01 ether; // 0.01 ETH
    uint256 public constant OWNER_DOWNLOAD_FEE_PERCENTAGE = 20; // 20% for owner

    uint256 public constant UPLOAD_POINTS = 3;
    uint256 public constant SHARE_POINTS = 10;
    uint256 public constant DOWNLOAD_POINTS = 20;
    uint256 public constant REFERRAL_POINTS = 1000; // Points for both referrer and referred

    uint256 public constant MAX_SINGLE_UPLOAD_1GB_BYTES = 300 * 1024 * 1024; // 300 MB
    uint256 public constant MAX_SINGLE_UPLOAD_5GB_BYTES = 600 * 1024 * 1024; // 600 MB
    uint256 public constant MAX_SINGLE_SHARE_1GB_BYTES = 300 * 1024 * 1024; // 300 MB
    uint256 public constant MAX_SINGLE_SHARE_5GB_BYTES = 600 * 1024 * 1024; // 600 MB
    uint256 public constant MAX_SHARE_POINTS_THRESHOLD_BYTES = 200 * 1024 * 1024; // 200 MB for 10 points

    // --- Structs ---
    struct File {
        string name;
        string ipfsHash; // Content Identifier from IPFS
        string fileType; // e.g., "image/jpeg", "video/mp4", "application/pdf"
        uint256 size; // Size in bytes
        address uploader;
        uint256 uploadTime;
        bool isPublic;
        uint256 downloadPrice; // In wei
    }

    // --- State Variables ---
    mapping(address => File[]) public userFiles; // Stores files uploaded by a user
    mapping(string => File) public filesByHash; // Maps IPFS hash to File struct for quick lookup
    mapping(address => uint256) public userStorageUsed; // Tracks current storage used by each user
    mapping(address => uint256) public userStorageLimit; // Tracks storage limit for each user
    mapping(address => uint256) public userPoints; // Tracks points for each user
    mapping(address => uint256) public userBalances; // Tracks Sepolia ETH balance held by the contract for each user

    // --- Referral System State Variables ---
    mapping(address => string) public userReferralCode; // User address => generated code
    mapping(string => address) public referralCodeToUser; // Code => User address (the referrer)
    mapping(address => uint256) public userReferralCount; // User address => count of referred users
    mapping(address => bool) public isRegistered; // Tracks if a user has gone through initial registration process

    uint256 private _referralCodeSalt = 0; // To help generate unique-ish codes

    // --- Events ---
    event Deposited(address indexed user, uint256 amount);
    event Withdrawn(address indexed user, uint256 amount);
    event StorageLimitUpgraded(address indexed user, uint256 newLimit, uint256 pricePaid);
    event FileUploaded(address indexed uploader, string indexed ipfsHash, string name, uint256 size, uint256 pointsEarned);
    event FileShared(address indexed sender, address indexed receiver, string indexed ipfsHash, uint256 feePaid, uint256 pointsEarned);
    event FileVisibilityChanged(string indexed ipfsHash, bool isPublic, uint256 newPrice);
    event FilePurchased(string indexed ipfsHash, address indexed purchaser, address indexed uploader, uint256 amountPaid, uint256 pointsEarned);
    event FileDeleted(address indexed deleter, string indexed ipfsHash, uint256 sizeFreed);
    event PointsEarned(address indexed user, uint256 points, string reason);
    event FeeCollected(address indexed collector, uint256 amount, string feeType); // New event for clarity

    // --- New Referral Events ---
    event UserRegistered(address indexed newUser, address indexed referrer, uint256 pointsAwarded);
    event ReferralCodeGenerated(address indexed user, string code);
    event ReferralCountUpdated(address indexed referrer, uint256 newCount); // For referral leaderboard
    event PointsLeaderboardUpdate(address indexed user, uint256 totalPoints); // For point leaderboard

    // --- Constructor ---
    constructor() Ownable(msg.sender) {}

    // --- Fallback and Receive functions ---
    receive() external payable {
        // User must be registered to deposit via direct ETH transfer
        require(isRegistered[msg.sender], "User must be registered to deposit funds.");
        userBalances[msg.sender] += msg.value; // Use += for clarity
        emit Deposited(msg.sender, msg.value);
    }

    fallback() external payable {
        // User must be registered to deposit via direct ETH transfer
        require(isRegistered[msg.sender], "User must be registered to deposit funds.");
        userBalances[msg.sender] += msg.value; // Use += for clarity
        emit Deposited(msg.sender, msg.value);
    }

    // --- Modifiers ---
    modifier isValidFileHash(string memory _ipfsHash) {
        require(bytes(_ipfsHash).length > 0, "Invalid IPFS hash");
        _;
    }

    modifier onlyUploader(string memory _ipfsHash) {
        require(filesByHash[_ipfsHash].uploader == msg.sender, "Only the uploader can perform this action");
        _;
    }

    // --- Referral System Functions ---

    /**
     * @dev Allows a new user to register, optionally with a referral code.
     * @param _referralCode The referral code used by the new user (empty string if none).
     * If valid, both referrer and referred gain REFERRAL_POINTS.
     */
    function registerUser(string memory _referralCode) public {
        require(!isRegistered[msg.sender], "User already registered");

        isRegistered[msg.sender] = true;
        userStorageLimit[msg.sender] = MAX_STORAGE_1GB_BYTES; // Set initial storage limit upon registration

        address referrerAddress = address(0);
        uint256 pointsToAward = 0; // Initialize points to 0 for the newUser

        // Only attempt to process referral if a code is provided and it's valid
        if (bytes(_referralCode).length > 0) {
            referrerAddress = referralCodeToUser[_referralCode];
            require(referrerAddress != address(0), "Invalid referral code"); // Code must exist
            require(referrerAddress != msg.sender, "Cannot refer yourself"); // Cannot refer self
            require(isRegistered[referrerAddress], "Referrer must be a registered user."); // Referrer must also be registered

            pointsToAward = REFERRAL_POINTS;

            // Award points to referred user (msg.sender)
            userPoints[msg.sender] += pointsToAward; // Use += for clarity
            emit PointsEarned(msg.sender, pointsToAward, "Referral (Referred)");
            emit PointsLeaderboardUpdate(msg.sender, userPoints[msg.sender]);

            // Award points to referrer
            userPoints[referrerAddress] += pointsToAward; // Use += for clarity
            emit PointsEarned(referrerAddress, pointsToAward, "Referral (Referrer)");
            emit PointsLeaderboardUpdate(referrerAddress, userPoints[referrerAddress]);

            // Increment referrer's referral count
            userReferralCount[referrerAddress] += 1; // Use += for clarity
            emit ReferralCountUpdated(referrerAddress, userReferralCount[referrerAddress]);
        }

        emit UserRegistered(msg.sender, referrerAddress, pointsToAward);
    }

    /**
     * @dev Allows a registered user to generate their unique 7-digit referral code.
     * @return The generated referral code.
     */
    function generateReferralCode() public returns (string memory) {
        require(isRegistered[msg.sender], "User must be registered to generate a referral code");
        require(bytes(userReferralCode[msg.sender]).length == 0, "You already have a referral code");

        string memory generatedCode;
        bool unique = false;
        uint256 attempts = 0;

        // Loop to ensure uniqueness for generated 7-digit code
        while (!unique && attempts < 10) { // Limit attempts to avoid excessive gas usage
            // Combine various unique and volatile data to create a seed for the code
            uint256 seed = uint256(keccak256(abi.encodePacked(
                block.timestamp,
                msg.sender,
                tx.origin,
                block.prevrandao, // FIX: Changed block.difficulty to block.prevrandao
                block.number,
                _referralCodeSalt++ // Increment salt to get a new seed each attempt
            )));

            // Extract a 7-digit number (0 to 9,999,999)
            uint256 codeNum = seed % 10_000_000;

            // Convert to string and pad with leading zeros to ensure it's always 7 characters
            generatedCode = codeNum.toString();
            while (bytes(generatedCode).length < 7) {
                generatedCode = string(abi.encodePacked("0", generatedCode));
            }

            if (referralCodeToUser[generatedCode] == address(0)) {
                unique = true;
            }
            attempts++;
        }

        require(unique, "Could not generate a unique referral code after multiple attempts. Please try again.");

        userReferralCode[msg.sender] = generatedCode;
        referralCodeToUser[generatedCode] = msg.sender;

        emit ReferralCodeGenerated(msg.sender, generatedCode);
        return generatedCode;
    }

    /**
     * @dev Returns the referral code for a given user.
     * @param _user The address of the user.
     * @return The referral code string.
     */
    function getReferralCode(address _user) public view returns (string memory) {
        return userReferralCode[_user];
    }

    /**
     * @dev Returns the number of users referred by a given user.
     * @param _user The address of the user (referrer).
     * @return The referral count.
     */
    function getReferralCount(address _user) public view returns (uint256) {
        return userReferralCount[_user];
    }

    // --- User Deposit and Withdrawal ---
    function deposit() public payable {
        require(isRegistered[msg.sender], "User must be registered to deposit funds.");
        require(msg.value > 0, "Deposit amount must be greater than zero");
        userBalances[msg.sender] += msg.value;
        emit Deposited(msg.sender, msg.value);
    }

    function withdraw(uint256 _amount) public {
        require(isRegistered[msg.sender], "User must be registered to withdraw funds.");
        require(userBalances[msg.sender] >= _amount, "Insufficient balance");
        require(_amount > 0, "Withdraw amount must be greater than zero");

        userBalances[msg.sender] -= _amount; // Use -= for clarity
        payable(msg.sender).transfer(_amount);
        emit Withdrawn(msg.sender, _amount);
    }

    function getUserBalance() public view returns (uint256) {
        return userBalances[msg.sender];
    }

    // --- Storage Upgrade ---
    function upgradeStorage() public {
        require(isRegistered[msg.sender], "User must be registered to upgrade storage.");
        require(userStorageLimit[msg.sender] == MAX_STORAGE_1GB_BYTES, "You are already on the 5GB plan or higher");
        require(userBalances[msg.sender] >= UPGRADE_TO_5GB_PRICE, "Insufficient Sepolia ETH balance for upgrade");

        userBalances[msg.sender] -= UPGRADE_TO_5GB_PRICE; // Use -= for clarity
        // The upgrade fee is implicitly kept by the contract (adds to address(this).balance)
        // If you want the owner to directly collect this too, you'd add:
        // userBalances[owner()] += UPGRADE_TO_5GB_PRICE;
        // For now, it remains in the contract's overall balance, collectable by owner via withdrawContractFees if implemented.

        userStorageLimit[msg.sender] = MAX_STORAGE_5GB_BYTES;
        emit StorageLimitUpgraded(msg.sender, MAX_STORAGE_5GB_BYTES, UPGRADE_TO_5GB_PRICE);
    }

    // --- File Upload ---
    function uploadFile(string memory _name, string memory _ipfsHash, string memory _fileType, uint256 _size)
        public
        isValidFileHash(_ipfsHash)
    {
        require(isRegistered[msg.sender], "User must be registered to upload files.");
        require(filesByHash[_ipfsHash].uploader == address(0), "File with this hash already exists");

        // userStorageLimit[msg.sender] is guaranteed to be set if isRegistered is true
        require(userStorageUsed[msg.sender] + _size <= userStorageLimit[msg.sender], "Storage limit exceeded");

        if (userStorageLimit[msg.sender] == MAX_STORAGE_1GB_BYTES) {
            require(_size <= MAX_SINGLE_UPLOAD_1GB_BYTES, "File size exceeds 1GB plan's single upload limit (300MB)");
        } else { // 5GB plan
            require(_size <= MAX_SINGLE_UPLOAD_5GB_BYTES, "File size exceeds 5GB plan's single upload limit (600MB)");
        }

        filesByHash[_ipfsHash] = File({
            name: _name,
            ipfsHash: _ipfsHash,
            fileType: _fileType,
            size: _size,
            uploader: msg.sender,
            uploadTime: block.timestamp,
            isPublic: false, // Default to private
            downloadPrice: 0 // Default no price
        });
        userFiles[msg.sender].push(filesByHash[_ipfsHash]); // Store a copy for user's uploaded list
        userStorageUsed[msg.sender] += _size; // Use += for clarity
        userPoints[msg.sender] += UPLOAD_POINTS; // Use += for clarity
        emit PointsLeaderboardUpdate(msg.sender, userPoints[msg.sender]); // Update for leaderboard

        emit FileUploaded(msg.sender, _ipfsHash, _name, _size, UPLOAD_POINTS);
        emit PointsEarned(msg.sender, UPLOAD_POINTS, "File Upload");
    }

    // --- File Sharing ---
    function shareFile(string memory _ipfsHash, address _receiver)
        public
        isValidFileHash(_ipfsHash)
        onlyUploader(_ipfsHash)
    {
        require(isRegistered[msg.sender], "User must be registered to share files.");
        require(_receiver != address(0) && _receiver != msg.sender, "Invalid receiver address");
        require(userBalances[msg.sender] >= SHARE_FEE, "Insufficient Sepolia ETH balance for sharing fee");
        require(isRegistered[_receiver], "Receiver must be a registered user to receive shared files.");

        File storage file = filesByHash[_ipfsHash];
        require(file.uploader != address(0), "File does not exist"); // Ensure file exists

        if (userStorageLimit[msg.sender] == MAX_STORAGE_1GB_BYTES) {
            require(file.size <= MAX_SINGLE_SHARE_1GB_BYTES, "File size exceeds 1GB plan's single share limit (300MB)");
        } else { // 5GB plan
            require(file.size <= MAX_SINGLE_SHARE_5GB_BYTES, "File size exceeds 5GB plan's single share limit (600MB)");
        }

        userBalances[msg.sender] -= SHARE_FEE; // Deduct fee from sender
        userBalances[owner()] += SHARE_FEE; // Owner collects the full share fee
        emit FeeCollected(owner(), SHARE_FEE, "Share Fee"); // Emit new event for transparency

        require(userStorageUsed[_receiver] + file.size <= userStorageLimit[_receiver], "Receiver's storage limit will be exceeded");

        // Add the file to the receiver's list if not already present
        bool found = false;
        for (uint i = 0; i < userFiles[_receiver].length; i++) {
            if (keccak256(abi.encodePacked(userFiles[_receiver][i].ipfsHash)) == keccak256(abi.encodePacked(_ipfsHash))) {
                found = true;
                break;
            }
        }
        if (!found) {
            userFiles[_receiver].push(file);
            userStorageUsed[_receiver] += file.size; // Use += for clarity
        }

        uint256 points = 0;
        if (file.size <= MAX_SHARE_POINTS_THRESHOLD_BYTES) { // 0-200MB range
            points = SHARE_POINTS;
        }
        userPoints[msg.sender] += points; // Use += for clarity
        emit PointsLeaderboardUpdate(msg.sender, userPoints[msg.sender]); // Update for leaderboard

        emit FileShared(msg.sender, _receiver, _ipfsHash, SHARE_FEE, points);
        emit PointsEarned(msg.sender, points, "File Share");
    }

    // --- File Visibility and Pricing (Business Model) ---
    function setFileVisibilityAndPrice(string memory _ipfsHash, bool _isPublic, uint256 _price)
        public
        isValidFileHash(_ipfsHash)
        onlyUploader(_ipfsHash)
    {
        require(isRegistered[msg.sender], "User must be registered to set file visibility and price.");
        require(_price >= MIN_DOWNLOAD_PRICE && _price <= MAX_DOWNLOAD_PRICE, "Download price must be between 0.00001 ETH and 0.01 ETH");
        File storage file = filesByHash[_ipfsHash];
        file.isPublic = _isPublic;
        file.downloadPrice = _price;
        emit FileVisibilityChanged(_ipfsHash, _isPublic, _price);
    }

    // --- File Download (Purchase) ---
    function purchaseFile(string memory _ipfsHash) public isValidFileHash(_ipfsHash) {
        require(isRegistered[msg.sender], "User must be registered to purchase files.");
        File storage file = filesByHash[_ipfsHash];
        require(file.uploader != address(0), "File does not exist");
        require(file.isPublic, "File is not public and cannot be purchased");
        require(msg.sender != file.uploader, "Uploader cannot purchase their own file");
        require(userBalances[msg.sender] >= file.downloadPrice, "Insufficient Sepolia ETH balance for download");
        require(isRegistered[file.uploader], "Uploader is not a registered user."); // Ensure uploader is registered

        // Calculate the owner's cut (20%) and uploader's cut (80%)
        uint256 totalDownloadPrice = file.downloadPrice;
        uint256 ownerCut = (totalDownloadPrice * OWNER_DOWNLOAD_FEE_PERCENTAGE) / 100;
        uint256 uploaderCut = totalDownloadPrice - ownerCut; // Remaining 80%

        // Deduct full payment from purchaser's internal balance
        userBalances[msg.sender] -= totalDownloadPrice; // Use -= for clarity

        // Add owner's cut to owner's internal balance
        userBalances[owner()] += ownerCut;
        emit FeeCollected(owner(), ownerCut, "Download Fee (Owner's Cut)"); // Emit new event for transparency

        // Add uploader's cut to uploader's internal balance
        userBalances[file.uploader] += uploaderCut;
        // No explicit event for uploader's income here, but FilePurchased covers it.

        // Purchaser's storage limit is set during registerUser
        require(userStorageUsed[msg.sender] + file.size <= userStorageLimit[msg.sender], "Your storage limit will be exceeded by downloading this file.");

        // Add the file to the purchaser's list if not already present
        bool found = false;
        for (uint i = 0; i < userFiles[msg.sender].length; i++) {
            if (keccak256(abi.encodePacked(userFiles[msg.sender][i].ipfsHash)) == keccak256(abi.encodePacked(_ipfsHash))) {
                found = true;
                break;
            }
        }
        if (!found) {
            userFiles[msg.sender].push(file);
            userStorageUsed[msg.sender] += file.size; // Use += for clarity
        }

        userPoints[msg.sender] += DOWNLOAD_POINTS; // Use += for clarity
        emit PointsLeaderboardUpdate(msg.sender, userPoints[msg.sender]); // Update for leaderboard

        emit FilePurchased(_ipfsHash, msg.sender, file.uploader, totalDownloadPrice, DOWNLOAD_POINTS);
        emit PointsEarned(msg.sender, DOWNLOAD_POINTS, "File Download");
    }

    // --- File Deletion ---
    function deleteFile(string memory _ipfsHash) public isValidFileHash(_ipfsHash) onlyUploader(_ipfsHash) {
        require(isRegistered[msg.sender], "User must be registered to delete files.");
        File storage file = filesByHash[_ipfsHash];
        uint256 sizeToFree = file.size;
        address uploaderAddress = file.uploader;

        delete filesByHash[_ipfsHash];

        uint256 index = 0;
        bool found = false;
        for (uint i = 0; i < userFiles[uploaderAddress].length; i++) {
            if (keccak256(abi.encodePacked(userFiles[uploaderAddress][i].ipfsHash)) == keccak256(abi.encodePacked(_ipfsHash))) {
                index = i;
                found = true;
                break;
            }
        }

        if (found) {
            // Replace the deleted element with the last element and pop the last element
            if (index < userFiles[uploaderAddress].length - 1) {
                userFiles[uploaderAddress][index] = userFiles[uploaderAddress][userFiles[uploaderAddress].length - 1];
            }
            userFiles[uploaderAddress].pop();
        }

        // Reduce storage used by the uploader
        userStorageUsed[uploaderAddress] -= sizeToFree; // Use -= for clarity

        emit FileDeleted(msg.sender, _ipfsHash, sizeToFree);
    }

    // --- View Functions ---
    function getMyFiles() public view returns (File[] memory) {
        require(isRegistered[msg.sender], "User must be registered to view files.");
        return userFiles[msg.sender];
    }

    function getFileByHash(string memory _ipfsHash) public view isValidFileHash(_ipfsHash) returns (string memory, string memory, uint256, address, uint256, bool, uint256) {
        File storage file = filesByHash[_ipfsHash];
        require(file.uploader != address(0), "File does not exist"); // Check if file exists
        return (file.name, file.fileType, file.size, file.uploader, file.uploadTime, file.isPublic, file.downloadPrice);
    }

    function getUserStorageInfo(address _user) public view returns (uint256 currentUsed, uint256 limit) {
        return (userStorageUsed[_user], userStorageLimit[_user]);
    }

    function getMyPoints() public view returns (uint256) {
        return userPoints[msg.sender];
    }

    // --- Owner Functions ---
    // Owner can withdraw accumulated fees (share fees, upgrade fees) from the contract balance
    // Funds from file purchases are directly transferred between userBalances, not contract balance
    function withdrawContractFees(uint256 _amount) public onlyOwner {
        require(address(this).balance >= _amount, "Insufficient contract balance to withdraw");
        require(_amount > 0, "Withdraw amount must be greater than zero");
        payable(owner()).transfer(_amount);
    }
}

